# Begin with getting all the subkeys
# http://technet.microsoft.com/en-us/library/ff730937.aspx
# http://powershell.com/cs/blogs/tips/archive/2009/12/03/dynamically-create-script-blocks.aspx
# http://technet.microsoft.com/sv-se/library/ee176852(en-us).aspx
[CmdletBinding()]
Param(
    [Parameter(Mandatory = $true)]
    [string]
    $SubKeyName
    ,[ValidateSet("true", "false")]
    [Parameter(Mandatory = $false)]
    [string] $VerboseLogging = 'false'
)

function Get-FileAgePropertyBags {	
    [CmdletBinding()]
    Param(
        [Parameter(Mandatory = $true)]
        [string]
        $SubKeyName
    )
    
    [bool] $verbose = $false
    if ($VerbosePreference -eq 'Continue') {
        $verbose = $true
    }

    $RegistryPath = 'hklm:\Software\Atea\FileAgeMonitoring\'
    $EventLogSourceName = 'Atea FileAge Monitor'
    $omApi = New-Object -ComObject 'MOM.ScriptAPI'
    if (![System.Diagnostics.EventLog]::SourceExists($EventLogSourceName)) {
        New-EventLog -Source $EventLogSourceName -LogName 'Application'
    }

    $fsUser = '$RunAs[Name="Atea.Windows.File.FileCreationTimeFolder.RunasProfile"]/UserName$'
    $fsPass = '$RunAs[Name="Atea.Windows.File.FileCreationTimeFolder.RunasProfile"]/Password$'

    if ($verbose) {
        Write-EventLog -LogName 'Application' -Source $EventLogSourceName -EventId '100' -Message "Running File Age Probe on '$SubKeyName' with RunAs '$fsUser'" -EntryType Information -Category 0
    }

    if ($fsUser -match '^.RunAs\[.*' -or $fsUser.Length -le 0 -or $fsUser -eq 'nt authority\system') {
        $credentials = $null
        $fsPass = $null
        $fsUser = $(whoami)

    } else {
        $credentials = [PSCredential]::new($fsUser, (ConvertTo-SecureString -String $fsPass -AsPlainText -Force))
    }

    if (Test-Path -Path $RegistryPath) {
        # Yes, we can read from $RegistryPath
        $SubKeys = Get-ChildItem $RegistryPath | Where-Object { $_.PSChildName -eq $SubKeyName }

        if ($null -ne $SubKeys) {
            # Found configuration keys, processing folders
            ForEach ($SubKey in $SubKeys) {
                $FriendlyName = ($SubKey.Name.Substring($SubKey.Name.LastIndexOfAny('\') + 1))
                $FolderPath = $SubKey.GetValue('FolderPath')
                $Recursive = $SubKey.GetValue('Recursive')
                $FilePattern = $SubKey.GetValue('FilePattern')
                $AgeInMinutes = $SubKey.GetValue('AgeInMinutes')
                $Operator = $SubKey.GetValue('Operator')
                $FileAgeAttribute = $SubKey.GetValue('FileAgeAttribute') # Don't know if I really need this

                if (($null -ne $FolderPath) -and ($null -ne $Recursive) -and ($null -ne $FilePattern) -and ($null -ne $AgeInMinutes) -and ($null -ne $Operator)) {
                    $Operator = $Operator.Trim()

                    switch ($Operator) {
                        '<' { $Operator = '-lt' }
                        '<=' { $Operator = '-le' }
                        '>' { $Operator = '-gt' }
                        '>=' { $Operator = '-ge' }
                        default { $Operator = '-gt' }
                    }

                    $summaryMessage = "File Age Monitor on '$FriendlyName', looking for '$FilePattern' where $FileAgeAttribute $Operator $AgeInMinutes in $FolderPath"
                    

                    if ($Recursive.Trim() -eq 'false') {
                        $Recursive = ''
                        $summaryMessage += "`n"
                    } else {
                        $Recursive = '-Recurse'
                        $summaryMessage += ", Recursive`n"
                    }

                    $summaryMessage += "as user: $fsUser`n`n"

                    if ($null -ne $credentials) {
                        New-PSDrive -Name $SubKeyName -PSProvider 'FileSystem' -Root $FolderPath -Credential $credentials
                    } else {
                        New-PSDrive -Name $SubKeyName -PSProvider 'FileSystem' -Root $FolderPath
                    }
                    
                    $command = "Get-ChildItem '$($SubKeyName):\' $Recursive -Attributes !Directory | Where-Object {(`$_.Name -like '$FilePattern') -and ((((Get-Date) - `$_.$FileAgeAttribute).TotalMinutes) $Operator $AgeInMinutes)}"
                    $command = $command -replace '  ', ' '
                    $ScriptBlock = $ExecutionContext.InvokeCommand.NewScriptBlock($command)
                    $Files = @(Invoke-Command -ScriptBlock $ScriptBlock) #DevSkim: ignore DS104456 

                    if ($Files.Count -gt 0) {
                        $summaryMessage += "Found $($Files.Count) matching files:`n"
                        #$summaryMessage += "File Name`tFile Path`t`tAge (minutes)`tTimeStamp`n"
                        ForEach ($File in $Files) {
                            $FileName = $File.Name
                            $FileFullname = $File.FullName
                            switch ($FileAgeAttribute) {
                                'CreationTime' { $FileDateTime = $File.CreationTime }
                                'LastWriteTime' { $FileDateTime = $File.LastWriteTime }
                                'LastAccessTime' { $FileDateTime = $File.LastAccessTime }
                            }
                            $FileAgeMinutes = [math]::Round(((Get-Date) - $FileDateTime).TotalMinutes, 0)
                            $summaryMessage += "$FileName`n`tMinutes since $($FileAgeAttribute): $FileAgeMinutes`n`tTimestamp: $($FileDateTime -f 'o')`n`tFile Path: $FileFullName`n`n"
                        }
                        #We have old files, and a message. Build the property bag
                        #issue45 quick fix, truncate $summaryMessage to avoid overloading the propertybag
                        $summaryMessage = $summaryMessage.subString(0, [System.Math]::Min(1024, $summaryMessage.Length))
                        $propertyBag = $omApi.CreatePropertyBag()
                        $propertyBag.AddValue('FolderFriendlyName', $FriendlyName)
                        $propertyBag.AddValue('Summary', $summaryMessage)
                        $propertyBag.AddValue('FileCount', $Files.Count)

                        $propertyBag
                        if ($verbose) {
                            Write-EventLog -LogName 'Application' -Source $EventLogSourceName -EventId '400' -Message $summaryMessage -EntryType Information -Category 0
                        }

                    } else {
                        # No matching files found
                        $propertyBag = $omApi.CreatePropertyBag()
                        $propertyBag.AddValue('FolderFriendlyName', $FriendlyName)
                        $propertyBag.AddValue('Summary', $summaryMessage)
                        $propertyBag.AddValue('FileCount', $Files.Count)

                        $propertyBag
                        if ($verbose) {
                            Write-EventLog -LogName 'Application' -Source $EventLogSourceName -EventId '200' -Message $summaryMessage -EntryType Information -Category 0
                        }
                    }
                    Remove-PSDrive -Name $SubKeyName
                    $credentials = $null
                }

            }
        } else {
            # Registry does not contain any configured File Age Monitoring
            # or we don't have access to read the keys
            $propertyBag = $omApi.CreatePropertyBag()
            $propertyBag
        }
    } else {
        #Nope, not able to read from the registry path
        Write-EventLog -LogName 'Application' -Source $EventLogSourceName -EventId '404' -Message "$RegistryPath is not found on this system, unable to read file age monitoring configuration.`n`nReturning an empty property bag." -EntryType Error -Category 0
        $propertyBag = $omApi.CreatePropertyBag()
        $propertyBag
    }
}

$debugHosts = @('Visual Studio Code Host')
if ($host.Name -in $debugHosts) {
    Get-FileAgePropertyBags -SubKeyName $SubKeyName -Verbose
} else {
    if ($VerboseLogging -eq 'true') {
        Get-FileAgePropertyBags -SubKeyName $SubKeyName -Verbose
    } else {
        Get-FileAgePropertyBags -SubKeyName $SubKeyName
    }
}

[CmdletBinding()]
param (
    [Parameter()]
    [string]
    $UNCPath = '\\server\share'
    , [Parameter()]
    [string]
    $ShareName = 'A ShareName'
    , [Parameter()]
    [double]
    $FreeSpaceErrorThreshold = 3
    , [Parameter()]
    [double]
    $FreeSpaceWarningThreshold = 10
    , [Parameter()]
    [string]
    $UsePercent = 'true'
)


function Get-FileShareFreeSpace {
    [CmdletBinding()]
    param (
        [Parameter()]
        [string]
        $UNCPath
        , [Parameter()]
        [string]
        $ShareName
    )

    $fsUser = '$RunAs[Name="Atea.Windows.File.FileShareMonitoring.RunasProfile"]/UserName$'
    $fsPass = '$RunAs[Name="Atea.Windows.File.FileShareMonitoring.RunasProfile"]/Password$'

    if ($fsUser -match '^.RunAs\[.*' -or $fsUser.Length -le 0 -or $fsUser -eq 'nt authority\system') {
        $credentials = $null
        $fsPass = $null
        $fsUser = $(whoami)

    } else {
        $credentials = [PSCredential]::new($fsUser, (ConvertTo-SecureString -String $fsPass -AsPlainText -Force))
    }

    $FolderPath = $UNCPath

    # Try to find a vacant drive-letter to use
    $charCodes = 67..90
    $drives = (Get-PSDrive).Name -as [string[]]
    foreach ($charCode in $charCodes) {
        $driveLetter = [char]$charCode
        if ($driveLetter -notin $drives) {
            $driveName = $driveLetter -as [string]
            break
        }
    }

    # Add a PSDrive with credentials (if needed). 
    # Needs to be -Persist, otherwise we won't get usage information
    if ($null -ne $credentials) {
        $drive = New-PSDrive -Name $driveName -PSProvider 'FileSystem' -Root $FolderPath -Credential $credentials -Persist
    } else {
        $drive = New-PSDrive -Name $driveName -PSProvider 'FileSystem' -Root $FolderPath -Persist
    }


    if ($null -ne $drive) {
        if ($null -ne $drive.Used -and $drive.Used -ne 0) {
            $usedGB = [math]::Round($($drive.Used / 1GB), 2) -as [double]
        }
        if ($null -ne $drive.Free) {
            $freeGB = [math]::Round($($drive.Free / 1GB), 2) -as [double]
        }
        if ($null -ne $usedGB -and $null -ne $freeGB) {
            $totalGB = [math]::Round($usedGB + $freeGB, 2) -as [double]
        }
        if ($null -ne $totalGB -and $totalGB -ne 0) {
            $usedPct = [math]::Round(($usedGB / $totalGB) * 100, 2) -as [double]
            $freePct = [math]::Round(($freeGB / $totalGB) * 100, 2) -as [double]
        }
        if ($null -eq $usedPct) { $usedPct = -1 -as [double] }
        if ($null -eq $usedGB) { $usedGB = -1 -as [double] }
        if ($null -eq $freePct) { $freePct = -1 -as [double] }
        if ($null -eq $freeGB) { $freeGB = -1 -as [double] }
        if ($null -eq $totalGB) { $totalGB = -1 -as [double] }
        $driveData = @{
            'ShareName' = $ShareName
            'Path'      = $UNCPath
            'TotalGB'   = $totalGB
            'UsedGB'    = $usedGB
            'UsedPct'   = $usedPct
            'FreeGB'    = $freeGB
            'FreePct'   = $freePct
            'RunAs'     = $fsUser
        }
    } else {
        $driveData = @{
            'ShareName' = $ShareName
            'RunAs'     = $fsUser
        }
    }

    Remove-PSDrive -Name $driveName

    return $driveData
}

function Set-FileShareStatus {
    [CmdletBinding()]
    param (
        [Parameter()]
        [hashtable]
        $DriveInfo
        , [Parameter()]
        [double]
        $FreeSpaceErrorThreshold = 3
        , [Parameter()]
        [double]
        $FreeSpaceWarningThreshold = 10
        , [Parameter()]
        [bool]
        $UsePercent = $true
    )

    if ($null -ne $DriveInfo) {
        if ($UsePercent = $true) {
            if ($DriveInfo.FreePct -le $FreeSpaceErrorThreshold -and $DriveInfo.FreePct -ne -1 -and $null -ne $DriveInfo.FreePct) {
                $status = 'ERROR'
            } elseif ($DriveInfo.FreePct -le $FreeSpaceWarningThreshold -and $DriveInfo.FreePct -ne -1 -and $null -ne $DriveInfo.FreePct) {
                $status = 'WARNING'
            } elseif ($DriveInfo.FreePct -gt $FreeSpaceWarningThreshold -and $DriveInfo.FreePct -ne -1 -and $null -ne $DriveInfo.FreePct) {
                $status = 'OK'
            } elseif ($DriveInfo.FreePct -eq -1 -or $null -eq $DriveInfo.FreePct) {
                $status = 'CONNECT_FAILED'
            } else {
                $status = 'UNDECIDED'
            }
        } elseif ($UsePercent = $false) {
            if ($DriveInfo.FreeGB -le $FreeSpaceErrorThreshold -and $DriveInfo.FreeGB -ne -1 -and $null -ne $DriveInfo.FreeGB) {
                $status = 'ERROR'
            } elseif ($DriveInfo.FreeGB -le $FreeSpaceWarningThreshold -and $DriveInfo.FreeGB -ne -1 -and $null -ne $DriveInfo.FreeGB) {
                $status = 'WARNING'
            } elseif ($DriveInfo.FreeGB -gt $FreeSpaceWarningThreshold -and $DriveInfo.FreeGB -ne -1 -and $null -ne $DriveInfo.FreeGB) {
                $status = 'OK'
            } elseif ($DriveInfo.FreeGB -eq -1 -or $null -eq $DriveInfo.FreeGB) {
                $status = 'CONNECT_FAILED'
            } else {
                $status = 'UNDECIDED'
            }
        } else {
            $status = 'WEIRD_INPUT'
        }
    } else {
        $status = 'DRIVEINFO_EMPTY'
    }

    $DriveInfo['Status'] = $status -as [string]
    return $DriveInfo
}

function Send-FSPropertyBag {
    [CmdletBinding()]
    param (
        [Parameter()]
        [hashtable]
        $DriveInfo
        , [Parameter()]
        [string]
        $UNCPath
    )

    $omApi = New-Object -ComObject 'MOM.ScriptAPI'
    $pb = $omApi.CreatePropertyBag()

    if (($DriveInfo.Status -ne 'CONNECT_FAILED') -and ($DriveInfo.Status -ne 'DRIVEINFO_EMPTY') -and ($null -ne $DriveInfo)) {
        $logMessage = "`n$($DriveInfo.ShareName)`nRunAs: $($DriveInfo.RunAs)`nDriveInfo:`n"
        $logMessage += ($DriveInfo | ConvertTo-Json)
    } else {
        $logMessage = "`nProbing $UNCPath failed"
    }
    $omApi.LogScriptEvent('FileShareSpace.ps1', 242, 0, $logMessage)
    $DriveInfo.Remove('RunAs') | Out-Null

    foreach ($key in $DriveInfo.Keys) {
        $pb.AddValue($key, $DriveInfo[$key])
    }

    if ($host.Name -in @('Visual Studio Code Host', 'Windows PowerShell ISE Host')) {
        $omApi.AddItem($pb)
        $omApi.ReturnItems()
    } else {
        $pb
    }
}

if ($UsePercent -eq 'false') {
    $usePct = $false
} else {
    $usePct = $true
}

$driveInfo = Get-FileShareFreeSpace -UNCPath $UNCPath -ShareName $ShareName
$driveInfo = Set-FileShareStatus -DriveInfo $driveInfo -FreeSpaceErrorThreshold $FreeSpaceErrorThreshold -FreeSpaceWarningThreshold $FreeSpaceWarningThreshold -UsePercent $usePct
Send-FSPropertyBag -DriveInfo $driveInfo -UNCPath $UNCPath

